import{_ as e,c as a,o as i,a as o}from"./app.4c1d16aa.js";const b=JSON.parse('{"title":"Time","description":"","frontmatter":{},"headers":[{"level":3,"title":"\u65F6\u95F4 -- MKJ","slug":"\u65F6\u95F4-mkj"},{"level":2,"title":"\u7FFB\u8BD1","slug":"\u7FFB\u8BD1"},{"level":2,"title":"\u751F\u8BCD\u7EDF\u8BA1","slug":"\u751F\u8BCD\u7EDF\u8BA1"},{"level":2,"title":"\u6B4C\u66F2\u80CC\u666F","slug":"\u6B4C\u66F2\u80CC\u666F"},{"level":2,"title":"Lyric \u6B4C\u8BCD","slug":"lyric-\u6B4C\u8BCD"}],"relativePath":"music/Time.md","lastUpdated":1657697433000}'),t={name:"music/Time.md"},r=o('<h1 id="time" tabindex="-1">Time <a class="header-anchor" href="#time" aria-hidden="true">#</a></h1><h3 id="\u65F6\u95F4-mkj" tabindex="-1">\u65F6\u95F4 -- MKJ <a class="header-anchor" href="#\u65F6\u95F4-mkj" aria-hidden="true">#</a></h3><h2 id="\u7FFB\u8BD1" tabindex="-1">\u7FFB\u8BD1 <a class="header-anchor" href="#\u7FFB\u8BD1" aria-hidden="true">#</a></h2><h4 id="oney-is-not-evil-by-itself" tabindex="-1">oney is not evil by itself <a class="header-anchor" href="#oney-is-not-evil-by-itself" aria-hidden="true">#</a></h4><p>\u94B1\u672C\u8EAB\u65E0\u7F6A</p><h4 id="its-just-paper-with-perceived-value-to-obtain-other-things-we-value-in-other-ways" tabindex="-1">Its just paper with <code>perceived</code> value to <code>obtain</code> other things we value in other ways <a class="header-anchor" href="#its-just-paper-with-perceived-value-to-obtain-other-things-we-value-in-other-ways" aria-hidden="true">#</a></h4><p>\u5B83\u53EA\u662F\u4E00\u5F20\u611F\u77E5\u4EF7\u503C\u7684\u7EB8\uFF0C\u6211\u4EEC\u7528\u6765\u83B7\u5F97\u5176\u4ED6\u7269\u54C1\u7684\u4E00\u79CD\u65B9\u5F0F</p><h4 id="if-not-money-what-is-evil-you-may-ask" tabindex="-1">If not money -- what is evil you may ask? <a class="header-anchor" href="#if-not-money-what-is-evil-you-may-ask" aria-hidden="true">#</a></h4><p>\u5982\u679C\u94B1\u65E0\u7F6A\uFF0C\u90A3\u4F60\u4F1A\u95EE\u90A3\u4EC0\u4E48\u6709\u7F6A\uFF1F</p><h4 id="evil-is-the-unquenchable-obsessive-and-moral-bending-desire-for-more" tabindex="-1">Evil is the <code>unquenchable</code>, <code>obsessive</code> and moral bending desire for more <a class="header-anchor" href="#evil-is-the-unquenchable-obsessive-and-moral-bending-desire-for-more" aria-hidden="true">#</a></h4><p>\u7F6A\u662F\u6B62\u4E0D\u4F4F\u7684\u3001\u4EE4\u4EBA\u7740\u8FF7\u7684\u3001\u9053\u5FB7\u626D\u66F2\u7684\u8D2A\u6B32</p><h4 id="evil-is-the-bottomless-soulless-and-obsessive-compulsive-pursuit-of-some-pot-of-gold" tabindex="-1">Evil is the bottomless, soulless and <code>obsessive-compulsive</code> pursuit of some pot of gold <a class="header-anchor" href="#evil-is-the-bottomless-soulless-and-obsessive-compulsive-pursuit-of-some-pot-of-gold" aria-hidden="true">#</a></h4><p>\u7F6A\u662F\u65E0\u6B62\u5883\u7684\u3001\u65E0\u7075\u9B42\u7684\u3001\u5F3A\u8FEB\u6027\u7684\u9010\u5229</p><h4 id="at-the-end-of-some-rainbow-which-doesn-t-exist" tabindex="-1">At the end of some rainbow which doesn&#39;t exist <a class="header-anchor" href="#at-the-end-of-some-rainbow-which-doesn-t-exist" aria-hidden="true">#</a></h4><p>\u90A3\u4E9B\u5E76\u4E0D\u5B58\u5728\u7684\u5F69\u8679\u4E4B\u5DC5</p><h4 id="evil-is-having-a-price-tag-for-your-heart-and-soul-in-exchange-for-financial-success-at-any-cost" tabindex="-1">Evil is having a price tag for your heart and soul in exchange for financial success at any cost <a class="header-anchor" href="#evil-is-having-a-price-tag-for-your-heart-and-soul-in-exchange-for-financial-success-at-any-cost" aria-hidden="true">#</a></h4><p>\u7F6A\u662F\u5728\u4EA4\u6613\u6210\u529F\u65F6\u5FC3\u548C\u7075\u9B42\u7684\u4EF7\u6807</p><h4 id="evil-is-trying-to-buy-happiness-again-and-again" tabindex="-1">Evil is trying to buy happiness, again and again <a class="header-anchor" href="#evil-is-trying-to-buy-happiness-again-and-again" aria-hidden="true">#</a></h4><p>\u7F6A\u8BD5\u56FE\u4E70\u56DE\u5E78\u798F\uFF0C\u5E76\u4E50\u6B64\u4E0D\u75B2</p><h4 id="until-all-of-those-fake-short-lived-mirages-of-emotions-are-gone" tabindex="-1">Until all of those fake, short lived mirages of emotions are gone <a class="header-anchor" href="#until-all-of-those-fake-short-lived-mirages-of-emotions-are-gone" aria-hidden="true">#</a></h4><p>\u77E5\u9053\u90A3\u4E9B\u865A\u5047\u3001\u77ED\u6682\u7684\u5FC3\u7075\u5E7B\u5883\u6D88\u5931</p><h4 id="make-more-time" tabindex="-1">Make more time <a class="header-anchor" href="#make-more-time" aria-hidden="true">#</a></h4><p>\u8D5A\u53D6\u66F4\u591A\u7684\u65F6\u95F4</p><h4 id="i-m-not-saying-you-can-t-be-financially-successful" tabindex="-1">I&#39;m not saying you can&#39;t be financially successful <a class="header-anchor" href="#i-m-not-saying-you-can-t-be-financially-successful" aria-hidden="true">#</a></h4><p>\u6211\u4E0D\u662F\u8BF4\u4F60\u4E0D\u8BE5\u8FFD\u6C42\u7ECF\u6D4E\u4E0A\u7684\u6210\u529F</p><h4 id="i-m-saying-have-a-greater-purpose-in-life-well-beyond-the-pursuit-of-financial-success" tabindex="-1">I&#39;m saying have a greater purpose in life well beyond the pursuit of financial success <a class="header-anchor" href="#i-m-saying-have-a-greater-purpose-in-life-well-beyond-the-pursuit-of-financial-success" aria-hidden="true">#</a></h4><p>\u800C\u662F\u8BF4\u6709\u4E00\u4E2A\u66F4\u4F1F\u5927\u7684\u751F\u547D\u76EE\u6807\u4F1A\u6BD4\u8FFD\u9010\u91D1\u94B1\u7684\u4E0A\u7684\u6210\u529F\u8D85\u7136</p><h4 id="your-soul-is-screaming-for-you-to-answer-your-true-calling" tabindex="-1">Your soul is screaming for you to answer your true calling <a class="header-anchor" href="#your-soul-is-screaming-for-you-to-answer-your-true-calling" aria-hidden="true">#</a></h4><p>\u4F60\u7684\u7075\u9B42\u6B63\u4E3A\u4F60\u6700\u771F\u5207\u7684\u547C\u558A\u800C\u5C16\u53EB</p><h4 id="you-can-change-today-if-you-redefine-what-success-is-to-you" tabindex="-1">You can change today if you redefine what success is to you <a class="header-anchor" href="#you-can-change-today-if-you-redefine-what-success-is-to-you" aria-hidden="true">#</a></h4><p>\u4F60\u53EF\u4EE5\u73B0\u5728\u6539\u53D8\uFF0C\u53EA\u8981\u4F60\u91CD\u65B0\u5B9A\u4E49\u4F55\u4E3A\u6210\u529F</p><h4 id="you-can-transform-your-damaged-realtionships-and-build-new-ones" tabindex="-1">You can transform your damaged realtionships and build new ones <a class="header-anchor" href="#you-can-transform-your-damaged-realtionships-and-build-new-ones" aria-hidden="true">#</a></h4><p>\u4F60\u53EF\u4EE5\u8F6C\u53D8\u4F60\u7834\u635F\u7684\u4EBA\u9645\u5173\u7CFB\u5E76\u91CD\u5EFA</p><h4 id="you-can-forgive-yourself-and-others-who-ve-hurt-you" tabindex="-1">You can forgive yourself and others who&#39;ve hurt you <a class="header-anchor" href="#you-can-forgive-yourself-and-others-who-ve-hurt-you" aria-hidden="true">#</a></h4><p>\u4F60\u53EF\u4EE5\u539F\u8C05\u81EA\u5DF1\u4EE5\u53CA\u90A3\u4E9B\u4F24\u5BB3\u8FC7\u4F60\u7684\u4EBA</p><h4 id="you-can-become-a-leader-by-mentoring-with-others-who-you-aspire-to-be-like" tabindex="-1">You can become a leader by <code>mentoring</code> with others who you aspire to be like <a class="header-anchor" href="#you-can-become-a-leader-by-mentoring-with-others-who-you-aspire-to-be-like" aria-hidden="true">#</a></h4><p>\u5728\u4F60\u6E34\u671B\u6210\u4E3A\u7684\u90A3\u4E9B\u4EBA\u7684\u8F85\u5BFC\u4E0B\uFF0C\u4F60\u53EF\u4EE5\u53D8\u6210\u4E00\u4E2A\u9886\u8896</p><h4 id="you-can-re-balance-your-priorities-in-life" tabindex="-1">You can re-balance your priorities in life <a class="header-anchor" href="#you-can-re-balance-your-priorities-in-life" aria-hidden="true">#</a></h4><p>\u4F60\u53EF\u4EE5\u8C03\u6574\u751F\u547D\u4E2D\u7684\u4F18\u5148\u7EA7</p><h4 id="you-can-heal-your-marriage-and-recreate-a-stronger-love-than-you-ever-thought-possible" tabindex="-1">You can heal your marriage and recreate a stronger love than you ever thought possible <a class="header-anchor" href="#you-can-heal-your-marriage-and-recreate-a-stronger-love-than-you-ever-thought-possible" aria-hidden="true">#</a></h4><p>\u4F60\u53EF\u4EE5\u6CBB\u6108\u4F60\u7684\u5A5A\u59FB\u5E76\u91CD\u5EFA\u6BD4\u4F60\u60F3\u8C61\u4E2D\u66F4\u5F3A\u70C8\u7684\u7231</p><h4 id="you-can-become-the-best-parent-possible-at-any-age-even-86" tabindex="-1">You can become the best parent possible at any age -- even 86 <a class="header-anchor" href="#you-can-become-the-best-parent-possible-at-any-age-even-86" aria-hidden="true">#</a></h4><p>\u4F60\u53EF\u4EE5\u53D8\u6210\u6700\u597D\u7684\u7236\u6BCD\uFF0C\u751A\u81F3\u523086\u5C81\u4ECD\u7136\u5982\u6B64</p><h4 id="but-don-t-wait-until-then" tabindex="-1">but don&#39;t wait until then... <a class="header-anchor" href="#but-don-t-wait-until-then" aria-hidden="true">#</a></h4><p>\u4F46\u522B\u7B49\u5230\u90A3\u65F6\u5019</p><h4 id="you-will-always-be-able-to-make-more-money" tabindex="-1">You will always be able to make more money <a class="header-anchor" href="#you-will-always-be-able-to-make-more-money" aria-hidden="true">#</a></h4><p>\u4F60\u4F1A\u6709\u80FD\u529B\u8D5A\u66F4\u591A\u7684\u94B1</p><h4 id="but-you-cannot-make-more-time" tabindex="-1">But you cannot make more time <a class="header-anchor" href="#but-you-cannot-make-more-time" aria-hidden="true">#</a></h4><p>\u4F46\u4F60\u5374\u4E0D\u80FD\u8D5A\u66F4\u591A\u7684\u65F6\u95F4</p><h2 id="\u751F\u8BCD\u7EDF\u8BA1" tabindex="-1">\u751F\u8BCD\u7EDF\u8BA1 <a class="header-anchor" href="#\u751F\u8BCD\u7EDF\u8BA1" aria-hidden="true">#</a></h2><table><thead><tr><th>\u5355\u8BCD</th><th>\u97F3\u6807</th><th>\u7FFB\u8BD1</th></tr></thead><tbody><tr><td>perceive</td><td>p\u0259r\u02C8si\u02D0v</td><td>v. \u5BDF\u89C9\u3001\u611F\u89C9\u3001\u7406\u89E3\u3001\u8BA4\u77E5</td></tr><tr><td>obtain</td><td>\u0259b\u02C8te\u026An</td><td>v. \u83B7\u5F97\u3001\u6D41\u884C</td></tr><tr><td>unqunchable</td><td>\u028Cn\u02C8kwent\u0283\u0259bl</td><td>adj. \u6B62\u4E0D\u4F4F\u7684\u3001\u4E0D\u53EF\u904F\u5236\u7684\u3001\u4E0D\u80FD\u7184\u706D\u7684</td></tr><tr><td>obsessive</td><td>\u0259b\u02C8ses\u026Av</td><td>adj. \u5F3A\u5236\u7684\u3001\u7740\u8FF7\u7684\u3001\u5206\u795E\u7684</td></tr><tr><td>obsessive-compulssive</td><td>\u0259b&#39;ses\u026Av k\u0259mp&#39;\u028Cls\u026Av</td><td>adj. \u5F3A\u8FEB\u89C2\u5FF5\u4E0E\u884C\u4E3A\u7684\uFF1B n.\u5F3A\u8FEB\u6027\u795E\u7ECF\u5B98\u80FD\u75C7\u60A3\u8005</td></tr><tr><td>mentor</td><td>\u02C8ment\u0254\u02D0r</td><td>n. \u6307\u5BFC\u8005\u3001\u826F\u5E08\u76CA\u53CB\uFF1B v. \u6307\u5BFC</td></tr></tbody></table><h2 id="\u6B4C\u66F2\u80CC\u666F" tabindex="-1">\u6B4C\u66F2\u80CC\u666F <a class="header-anchor" href="#\u6B4C\u66F2\u80CC\u666F" aria-hidden="true">#</a></h2><blockquote><p>\u300ATime\u300B\u662F\u7F8E\u56FD\u7535\u97F3\u5236\u4F5C\u4EBAMKJ\u5236\u4F5C\u7684\u4E00\u9996\u6B4C\u66F2\uFF0C\u53D1\u884C\u4E8E2015\u5E7404\u670825\u65E5\u3002<br> \u6B4C\u4E2D\u7684\u72EC\u767D\u6765\u81EA\u6469\u6839\xB7\u5F17\u91CC\u66FC\u5728\u7EAA\u5F55\u7247\u300AThrough The Wormhole With Morgan Freeman\u300B\uFF08\u300A\u4E0E\u6469\u6839\xB7\u5F17\u91CC\u66FC\u4E00\u8D77\u7A7F\u8D8A\u866B\u6D1E\u300B\uFF09\u4E2D\u7684\u4E00\u6BB5\u53F0\u8BCD\u3002<br> \u300ATime\u300B\u57282018\u5E7411\u67088\u65E5\u88AB\u539F\u66F2\u4F5C\u8005MKJ\u4FEE\u6539\u4E3A\u300ATime offcial\u300B\u97F3\u4E50\u4E0D\u53D8\uFF0C\u6B4C\u8BCD\u91CD\u7F16\uFF0C\u539F\u56E0\u672A\u77E5</p></blockquote><h2 id="lyric-\u6B4C\u8BCD" tabindex="-1">Lyric \u6B4C\u8BCD <a class="header-anchor" href="#lyric-\u6B4C\u8BCD" aria-hidden="true">#</a></h2><p>Money is not evil by itself</p><p>Its just paper with perceived value to obtain other things we value in other ways</p><p>If not money -- what is evil you may ask?</p><p>Evil is the unquenchable, obsessive and moral bending desire for more</p><p>Evil is the bottomless, soulless and obsessive-compulsive pursuit of some pot of gold</p><p>At the end of some rainbow which doesn&#39;t exist</p><p>Evil is having a price tag for your heart and soul in exchange for financial success at any cost</p><p>Evil is trying to buy happiness, again and again</p><p>Until all of those fake, short lived mirages of emotions are gone</p><p>Make more time</p><p>I&#39;m not saying you can&#39;t be financially successful</p><p>I&#39;m saying have a greater purpose in life well beyond the pursuit of financial success</p><p>Your soul is screaming for you to answer your true calling</p><p>You can change today if you redefine what success is to you</p><p>You can transform your damaged relationships and build new ones</p><p>You can forgive yourself and others who&#39;ve hurt you</p><p>You can become a leader by mentoring with others who you aspire to be like</p><p>You can re-balance your priorities in life</p><p>You can heal your marriage and recreate a stronger love than you ever thought possible</p><p>You can become the best parent possible at any age -- even 86</p><p>but don&#39;t wait until then...</p><p>You will always be able to make more money</p><p>But you cannot make more time</p>',77),n=[r];function s(h,d,l,u,c,p){return i(),a("div",null,n)}var m=e(t,[["render",s]]);export{b as __pageData,m as default};
